import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { aiService } from "./services/ai-service";
import { seoService } from "./services/seo-service";
import { approvalWorkflowService } from "./services/approval-workflow";
import { insertWebsiteSchema, insertContentSchema } from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  // Websites routes
  app.get("/api/websites", async (req, res) => {
    try {
      const websites = await storage.getWebsites();
      res.json(websites);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch websites" });
    }
  });

  app.get("/api/websites/:id", async (req, res) => {
    try {
      const website = await storage.getWebsite(req.params.id);
      if (!website) {
        return res.status(404).json({ message: "Website not found" });
      }
      res.json(website);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch website" });
    }
  });

  app.post("/api/websites", async (req, res) => {
    try {
      const validatedData = insertWebsiteSchema.parse(req.body);
      const website = await storage.createWebsite(validatedData);
      res.status(201).json(website);
    } catch (error) {
      res.status(400).json({ message: "Invalid website data" });
    }
  });

  app.put("/api/websites/:id", async (req, res) => {
    try {
      const website = await storage.updateWebsite(req.params.id, req.body);
      if (!website) {
        return res.status(404).json({ message: "Website not found" });
      }
      res.json(website);
    } catch (error) {
      res.status(500).json({ message: "Failed to update website" });
    }
  });

  app.delete("/api/websites/:id", async (req, res) => {
    try {
      const deleted = await storage.deleteWebsite(req.params.id);
      if (!deleted) {
        return res.status(404).json({ message: "Website not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete website" });
    }
  });

  // Content routes
  app.get("/api/websites/:id/content", async (req, res) => {
    try {
      const content = await storage.getContentByWebsite(req.params.id);
      res.json(content);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch content" });
    }
  });

  app.post("/api/content/generate", async (req, res) => {
    try {
      const { websiteId, topic, keywords, tone, wordCount, brandVoice, targetAudience, eatCompliance } = req.body;

      if (!websiteId || !topic) {
        return res.status(400).json({ message: "Website ID and topic are required" });
      }

      const result = await aiService.generateContent({
        websiteId,
        topic,
        keywords: keywords || [],
        tone: tone || "professional",
        wordCount: wordCount || 800,
        seoOptimized: true,
        brandVoice: brandVoice || "professional",
        targetAudience,
        eatCompliance: eatCompliance || false
      });

      // Save the generated content (defaults to pending_approval)
      const content = await storage.createContent({
        websiteId,
        title: result.title,
        body: result.content,
        excerpt: result.excerpt,
        metaDescription: result.metaDescription,
        metaTitle: result.metaTitle,
        aiModel: "gpt-4o",
        seoKeywords: result.keywords,
        seoScore: result.seoScore,
        readabilityScore: result.readabilityScore,
        brandVoiceScore: result.brandVoiceScore,
        eatCompliance: result.eatCompliance,
      });

      // Log the activity
      await storage.createActivityLog({
        websiteId,
        type: "content_generated",
        description: `AI content generated: "${result.title}" (${result.tokensUsed} tokens, $${result.costUsd})`,
        metadata: { 
          contentId: content.id, 
          aiModel: "gpt-4o",
          tokensUsed: result.tokensUsed,
          costUsd: result.costUsd,
          qualityChecks: result.qualityChecks
        }
      });

      res.json({ content, aiResult: result });
    } catch (error) {
      console.error("Content generation error:", error);
      res.status(500).json({ message: error instanceof Error ? error.message : "Failed to generate content" });
    }
  });

  app.post("/api/content/:id/publish", async (req, res) => {
    try {
      const content = await storage.updateContent(req.params.id, {
        status: "published",
        publishDate: new Date()
      });

      if (!content) {
        return res.status(404).json({ message: "Content not found" });
      }

      // Log the activity
      await storage.createActivityLog({
        websiteId: content.websiteId,
        type: "content_published",
        description: `Content published: "${content.title}"`,
        metadata: { contentId: content.id }
      });

      res.json(content);
    } catch (error) {
      res.status(500).json({ message: "Failed to publish content" });
    }
  });

  // SEO routes
  app.get("/api/websites/:id/seo-reports", async (req, res) => {
    try {
      const reports = await storage.getSeoReportsByWebsite(req.params.id);
      res.json(reports);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch SEO reports" });
    }
  });

  app.post("/api/websites/:id/seo-analysis", async (req, res) => {
    try {
      const website = await storage.getWebsite(req.params.id);
      if (!website) {
        return res.status(404).json({ message: "Website not found" });
      }

      const analysis = await seoService.analyzeWebsite(website.url);

      // Save the report
      const report = await storage.createSeoReport({
        websiteId: req.params.id,
        score: analysis.score,
        issues: analysis.issues,
        recommendations: analysis.recommendations,
        pageSpeedScore: analysis.pageSpeedScore
      });

      // Update website SEO score
      await storage.updateWebsite(req.params.id, {
        seoScore: analysis.score
      });

      // Log the activity
      await storage.createActivityLog({
        websiteId: req.params.id,
        type: "seo_analysis",
        description: `SEO analysis completed (Score: ${analysis.score})`,
        metadata: { reportId: report.id, score: analysis.score }
      });

      res.json(analysis);
    } catch (error) {
      console.error("SEO analysis error:", error);
      res.status(500).json({ message: error instanceof Error ? error.message : "Failed to perform SEO analysis" });
    }
  });

  app.post("/api/websites/:id/seo-autofix", async (req, res) => {
    try {
      const { issueType } = req.body;
      const result = await seoService.performAutoFix(req.params.id, issueType);

      if (result.success) {
        await storage.createActivityLog({
          websiteId: req.params.id,
          type: "seo_autofix",
          description: `Auto-fix applied: ${issueType}`,
          metadata: { issueType, success: true }
        });
      }

      res.json(result);
    } catch (error) {
      res.status(500).json({ message: "Failed to apply auto-fix" });
    }
  });

  // Activity logs
  app.get("/api/activity-logs", async (req, res) => {
    try {
      const websiteId = req.query.websiteId as string;
      const logs = await storage.getActivityLogs(websiteId);
      res.json(logs);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch activity logs" });
    }
  });

  // Dashboard stats
  app.get("/api/dashboard/stats", async (req, res) => {
    try {
      const websites = await storage.getWebsites();
      const allLogs = await storage.getActivityLogs();

      const stats = {
        activeWebsites: websites.length,
        contentGenerated: allLogs.filter(log => log.type === "content_generated" || log.type === "content_published").length,
        avgSeoScore: Math.round(websites.reduce((sum, w) => sum + w.seoScore, 0) / websites.length) || 0,
        scheduledPosts: allLogs.filter(log => log.type === "content_scheduled").length
      };

      res.json(stats);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch dashboard stats" });
    }
  });

  // Performance data for charts
  app.get("/api/dashboard/performance", async (req, res) => {
    try {
      // Generate mock performance data for the last 30 days
      const days = 7;
      const data = [];
      const baseScore = 75;

      for (let i = days - 1; i >= 0; i--) {
        const date = new Date();
        date.setDate(date.getDate() - i);
        const variation = Math.random() * 10 - 5; // +/- 5 points
        const score = Math.max(70, Math.min(100, baseScore + variation + (i * 2))); // Slight upward trend

        data.push({
          date: date.toISOString().split('T')[0],
          score: Math.round(score)
        });
      }

      res.json(data);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch performance data" });
    }
  });

  // Content Approval Workflow Routes
  app.get("/api/content/pending-approval", async (req, res) => {
    try {
      const pendingContent = await approvalWorkflowService.getPendingApprovals();
      res.json(pendingContent);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch pending approvals" });
    }
  });

  app.post("/api/content/:id/submit-for-approval", async (req, res) => {
    try {
      const result = await approvalWorkflowService.submitForApproval(req.params.id);
      res.json(result);
    } catch (error) {
      res.status(500).json({ message: "Failed to submit for approval" });
    }
  });

  app.post("/api/content/:id/approve", async (req, res) => {
    try {
      const { reviewerId, feedback, qualityScore } = req.body;

      if (!reviewerId) {
        return res.status(400).json({ message: "Reviewer ID is required" });
      }

      const result = await approvalWorkflowService.processApproval({
        contentId: req.params.id,
        reviewerId,
        decision: "approved",
        feedback,
        qualityScore
      });

      res.json(result);
    } catch (error) {
      res.status(500).json({ message: "Failed to approve content" });
    }
  });

  app.post("/api/content/:id/reject", async (req, res) => {
    try {
      const { reviewerId, feedback, qualityScore } = req.body;

      if (!reviewerId) {
        return res.status(400).json({ message: "Reviewer ID is required" });
      }

      const result = await approvalWorkflowService.processApproval({
        contentId: req.params.id,
        reviewerId,
        decision: "rejected",
        feedback,
        qualityScore
      });

      res.json(result);
    } catch (error) {
      res.status(500).json({ message: "Failed to reject content" });
    }
  });

  app.post("/api/content/:id/publish", async (req, res) => {
    try {
      const { publishNow, scheduledDate, performBackup } = req.body;

      const result = await approvalWorkflowService.publishApprovedContent(
        req.params.id,
        {
          publishNow: publishNow || false,
          scheduledDate: scheduledDate ? new Date(scheduledDate) : undefined,
          performBackup: performBackup || true
        }
      );

      res.json(result);
    } catch (error) {
      res.status(500).json({ message: "Failed to publish content" });
    }
  });

  // Emergency Controls
  app.post("/api/websites/:id/emergency-stop", async (req, res) => {
    try {
      const { reason } = req.body;

      if (!reason) {
        return res.status(400).json({ message: "Emergency stop reason is required" });
      }

      const result = await approvalWorkflowService.emergencyStop(req.params.id, reason);
      res.json(result);
    } catch (error) {
      res.status(500).json({ message: "Failed to activate emergency stop" });
    }
  });

  // AI Usage Tracking
  app.get("/api/ai-usage/:websiteId", async (req, res) => {
    try {
      const usage = await storage.aiUsageTracking
        .select()
        .from(storage.aiUsageTracking)
        .where(eq(storage.aiUsageTracking.websiteId, req.params.websiteId))
        .orderBy(desc(storage.aiUsageTracking.createdAt))
        .limit(100);

      res.json(usage);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch AI usage data" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  users,
  websites,
  content,
  seoReports,
  activityLogs,
  clientReports,
  contentApprovals,
  securityAudits,
  aiUsageTracking,
  seoAudits,
  contentSchedule,
  backups,
  type User, 
  type UpsertUser,
  type InsertUser, 
  type Website, 
  type InsertWebsite,
  type Content,
  type InsertContent,
  type SeoReport,
  type InsertSeoReport,
  type ActivityLog,
  type InsertActivityLog,
  type ClientReport,
  type InsertClientReport,
  type ContentApproval,
  type InsertContentApproval,
  type SecurityAudit,
  type InsertSecurityAudit,
  type AiUsageTracking,
  type InsertAiUsageTracking,
  type SeoAudit,
  type InsertSeoAudit,
  type ContentSchedule,
  type InsertContentSchedule,
  type Backup,
  type InsertBackup
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and } from "drizzle-orm";
import { wordPressAuthService } from "./services/wordpress-auth";

export interface IStorage {
  // Users (Required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;
  createUser(user: InsertUser): Promise<User>;

  // Websites
  getWebsites(): Promise<Website[]>;
  getWebsite(id: string): Promise<Website | undefined>;
  createWebsite(website: InsertWebsite): Promise<Website>;
  updateWebsite(id: string, website: Partial<Website>): Promise<Website | undefined>;
  deleteWebsite(id: string): Promise<boolean>;

  // Content
  getContentByWebsite(websiteId: string): Promise<Content[]>;
  getContent(id: string): Promise<Content | undefined>;
  createContent(content: InsertContent): Promise<Content>;
  updateContent(id: string, content: Partial<Content>): Promise<Content | undefined>;
  getPendingApprovalContent(): Promise<Content[]>;

  // SEO Reports
  getSeoReportsByWebsite(websiteId: string): Promise<SeoReport[]>;
  getLatestSeoReport(websiteId: string): Promise<SeoReport | undefined>;
  createSeoReport(report: InsertSeoReport): Promise<SeoReport>;

  // Activity Logs
  getActivityLogs(websiteId?: string): Promise<ActivityLog[]>;
  createActivityLog(log: InsertActivityLog): Promise<ActivityLog>;

  // Client Reports
  getClientReports(websiteId: string): Promise<ClientReport[]>;
  createClientReport(report: InsertClientReport): Promise<ClientReport>;

  // Enhanced features
  createContentApproval(approval: InsertContentApproval): Promise<ContentApproval>;
  createSecurityAudit(audit: InsertSecurityAudit): Promise<SecurityAudit>;
  trackAiUsage(usage: InsertAiUsageTracking): Promise<AiUsageTracking>;
  createSeoAudit(audit: InsertSeoAudit): Promise<SeoAudit>;
  getContentSchedule(websiteId: string): Promise<ContentSchedule[]>;
  createContentSchedule(schedule: InsertContentSchedule): Promise<ContentSchedule>;
  createBackup(backup: InsertBackup): Promise<Backup>;
}

export class DatabaseStorage implements IStorage {
  constructor() {
    // Initialize with sample data if needed
    this.initializeSampleData();
  }

  private async initializeSampleData() {
    try {
      // Check if sample data already exists
      const existingWebsites = await db.select().from(websites).limit(1);
      if (existingWebsites.length > 0) return;

      // Create sample websites with secure Application Password format
      const sampleWebsites = [
        {
          name: "TechBlog.com",
          url: "https://techblog.com",
          wpApplicationName: "AI Content Manager - TechBlog",
          wpApplicationPassword: "demo-encrypted-app-password-1", // Would be encrypted in real use
          wpUsername: "admin",
          aiModel: "gpt-4o",
          autoPosting: false, // Default to manual approval
          requireApproval: true,
          status: "active",
          seoScore: 92,
          contentCount: 24,
          brandVoice: "technical",
          targetAudience: "developers",
        },
        {
          name: "E-Commerce.store",
          url: "https://e-commerce.store",
          wpApplicationName: "AI Content Manager - ECommerce",
          wpApplicationPassword: "demo-encrypted-app-password-2",
          wpUsername: "admin",
          aiModel: "gpt-4o",
          autoPosting: false,
          requireApproval: true,
          status: "processing",
          seoScore: 78,
          contentCount: 18,
          brandVoice: "friendly",
          targetAudience: "online shoppers",
        },
        {
          name: "RestaurantSite.com",
          url: "https://restaurantsite.com",
          wpApplicationName: "AI Content Manager - Restaurant",
          wpApplicationPassword: "demo-encrypted-app-password-3",
          wpUsername: "admin",
          aiModel: "claude-3",
          autoPosting: false,
          requireApproval: true,
          status: "issues",
          seoScore: 65,
          contentCount: 12,
          brandVoice: "warm",
          targetAudience: "local diners",
        },
      ];

      await db.insert(websites).values(sampleWebsites);

      // Sample activity logs
      const sampleActivities = [
        {
          websiteId: null, // System-wide activity
          type: "system_init",
          description: "WordPress AI automation platform initialized",
          metadata: { version: "2.0", secure: true },
        },
        {
          websiteId: null,
          type: "security_upgrade",
          description: "Upgraded to WordPress Application Passwords authentication",
          metadata: { previousAuth: "username/password", newAuth: "application_passwords" },
        },
      ];

      await db.insert(activityLogs).values(sampleActivities);
    } catch (error) {
      console.error('Failed to initialize sample data:', error);
    }
  }

  // Users (Required for Replit Auth)
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: userData,
      })
      .returning();
    return user;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  // Websites
  async getWebsites(): Promise<Website[]> {
    return await db
      .select()
      .from(websites)
      .orderBy(desc(websites.updatedAt));
  }

  async getWebsite(id: string): Promise<Website | undefined> {
    const [website] = await db.select().from(websites).where(eq(websites.id, id));
    return website;
  }

  async createWebsite(insertWebsite: InsertWebsite): Promise<Website> {
    // Encrypt the application password before storing
    const encryptedPassword = wordPressAuthService.encryptCredentials({
      applicationName: insertWebsite.wpApplicationName,
      applicationPassword: insertWebsite.wpApplicationPassword,
      username: insertWebsite.wpUsername || 'admin'
    });

    const [website] = await db
      .insert(websites)
      .values({
        ...insertWebsite,
        wpApplicationPassword: encryptedPassword.encrypted,
        status: "active",
        seoScore: 0,
        contentCount: 0,
      })
      .returning();

    // Log activity
    await this.createActivityLog({
      websiteId: website.id,
      type: "website_connected",
      description: `Website connected: ${website.name}`,
      metadata: { url: website.url, secure: true },
    });

    // Create security audit log
    await this.createSecurityAudit({
      websiteId: website.id,
      action: "website_connection",
      success: true,
      metadata: { 
        authType: "application_password", 
        applicationName: insertWebsite.wpApplicationName 
      },
    });

    return website;
  }

  async updateWebsite(id: string, updates: Partial<Website>): Promise<Website | undefined> {
    const [website] = await db
      .update(websites)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(websites.id, id))
      .returning();
    return website;
  }

  async deleteWebsite(id: string): Promise<boolean> {
    const result = await db.delete(websites).where(eq(websites.id, id));
    return (result.rowCount ?? 0) > 0;
  }

  // Content
  async getContentByWebsite(websiteId: string): Promise<Content[]> {
    return await db
      .select()
      .from(content)
      .where(eq(content.websiteId, websiteId))
      .orderBy(desc(content.createdAt));
  }

  async getContent(id: string): Promise<Content | undefined> {
    const [contentItem] = await db.select().from(content).where(eq(content.id, id));
    return contentItem;
  }

  async getPendingApprovalContent(): Promise<Content[]> {
    return await db
      .select()
      .from(content)
      .where(eq(content.status, "pending_approval"))
      .orderBy(desc(content.createdAt));
  }

  async createContent(insertContent: InsertContent): Promise<Content> {
    const [contentItem] = await db
      .insert(content)
      .values({
        ...insertContent,
        status: "pending_approval", // Default to requiring approval
      })
      .returning();
    return contentItem;
  }

  async updateContent(id: string, updates: Partial<Content>): Promise<Content | undefined> {
    const [contentItem] = await db
      .update(content)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(content.id, id))
      .returning();
    return contentItem;
  }

  // SEO Reports
  async getSeoReportsByWebsite(websiteId: string): Promise<SeoReport[]> {
    return await db
      .select()
      .from(seoReports)
      .where(eq(seoReports.websiteId, websiteId))
      .orderBy(desc(seoReports.createdAt));
  }

  async getLatestSeoReport(websiteId: string): Promise<SeoReport | undefined> {
    const [report] = await db
      .select()
      .from(seoReports)
      .where(eq(seoReports.websiteId, websiteId))
      .orderBy(desc(seoReports.createdAt))
      .limit(1);
    return report;
  }

  async createSeoReport(insertReport: InsertSeoReport): Promise<SeoReport> {
    const [report] = await db
      .insert(seoReports)
      .values(insertReport)
      .returning();
    return report;
  }

  // Activity Logs
  async getActivityLogs(websiteId?: string): Promise<ActivityLog[]> {
    if (websiteId) {
      return await db
        .select()
        .from(activityLogs)
        .where(eq(activityLogs.websiteId, websiteId))
        .orderBy(desc(activityLogs.createdAt));
    }

    return await db
      .select()
      .from(activityLogs)
      .orderBy(desc(activityLogs.createdAt));
  }

  async createActivityLog(insertLog: InsertActivityLog): Promise<ActivityLog> {
    const [log] = await db
      .insert(activityLogs)
      .values(insertLog)
      .returning();
    return log;
  }

  // Client Reports
  async getClientReports(websiteId: string): Promise<ClientReport[]> {
    return await db
      .select()
      .from(clientReports)
      .where(eq(clientReports.websiteId, websiteId))
      .orderBy(desc(clientReports.generatedAt));
  }

  async createClientReport(insertReport: InsertClientReport): Promise<ClientReport> {
    const [report] = await db
      .insert(clientReports)
      .values(insertReport)
      .returning();
    return report;
  }

  // Enhanced Security and Management Features
  async createContentApproval(approval: InsertContentApproval): Promise<ContentApproval> {
    const [approvalRecord] = await db
      .insert(contentApprovals)
      .values(approval)
      .returning();
    return approvalRecord;
  }

  async createSecurityAudit(audit: InsertSecurityAudit): Promise<SecurityAudit> {
    const [auditRecord] = await db
      .insert(securityAudits)
      .values(audit)
      .returning();
    return auditRecord;
  }

  async trackAiUsage(usage: InsertAiUsageTracking): Promise<AiUsageTracking> {
    const [usageRecord] = await db
      .insert(aiUsageTracking)
      .values(usage)
      .returning();
    return usageRecord;
  }

  async createSeoAudit(audit: InsertSeoAudit): Promise<SeoAudit> {
    const [auditRecord] = await db
      .insert(seoAudits)
      .values(audit)
      .returning();
    return auditRecord;
  }

  async getContentSchedule(websiteId: string): Promise<ContentSchedule[]> {
    return await db
      .select()
      .from(contentSchedule)
      .where(eq(contentSchedule.websiteId, websiteId))
      .orderBy(desc(contentSchedule.scheduledDate));
  }

  async createContentSchedule(schedule: InsertContentSchedule): Promise<ContentSchedule> {
    const [scheduleRecord] = await db
      .insert(contentSchedule)
      .values(schedule)
      .returning();
    return scheduleRecord;
  }

  async createBackup(backup: InsertBackup): Promise<Backup> {
    const [backupRecord] = await db
      .insert(backups)
      .values(backup)
      .returning();
    return backupRecord;
  }
}

export const storage = new DatabaseStorage();

